mapper.update:
update member
set M_PASS = #{M_PASS}, M_MOBILE_TEL = #{M_MOBILE_TEL}, M_EMAIL = #{M_EMAIL}, 
M_POSTCODE = #{M_POSTCODE}, M_ADDRESS = #{M_ADDRESS}, M_ADDRESS2 = #{M_ADDRESS2}
where M_CODE = #{M_CODE}

mapper.select:
 select *
 
	from (select * from member where id != 'admin'
             
                and
                
                   M_NAME
                   M_PART_C
                   M_MOBILE_TEL
                 
                   like #{search_word}
             
          order by id
             )b
       )
 where rnum >= #{start} and rnum < = #{end}

select mem.M_CODE, mem.M_PART_C, mem.M_NAME, mem.M_LEVEL, mem.M_EMAIL, mem.M_MOBILE_TEL, bookmark.M_BOOKMARK, bookmark.M_OWNER from bookmark right join (select * from ( select rownum rnum, b.* from (select * from member where M_CODE != 'admin'
and
M_CODE M_PART_C M_NAME M_LEVEL
like #{search_word}
order by M_CODE )b ) where rnum > = #{start} and rnum < = #{end}) mem on bookmark.m_code = mem.m_code and bookmark.m_owner = #{owner}

mapper.delete:
delete from bookmark
where
m_owner = #{owner} and m_code = #{user}
